mapper.select:
SELECT categrpno, name, seqno, visible, rdate 
FROM categrp
WHERE categrpno = #{categrpno}

mapper.update:
UPDATE categrp
SET name=#{name}, seqno=#{seqno}, visible=#{visible}
WHERE categrpno=#{categrpno}

UPDATE categrp
SET seqno = seqno - 1
WHERE categrpno = #{categrpno}

UPDATE categrp
SET seqno = seqno + 1
WHERE categrpno = #{categrpno}

UPDATE categrp
SET visible=#{visible}
WHERE categrpno=#{categrpno}
