mapper.select:
SELECT exhino, name, cnt, rdate, seqno, visible 
FROM exhi 
WHERE exhino=#{exhino}

mapper.update:
UPDATE exhi 
SET name=#{name}, seqno=#{seqno} 
WHERE exhino=#{exhino}

UPDATE exhi
SET seqno = seqno - 1
WHERE exhino = #{exhino}

UPDATE exhi
SET seqno = seqno + 1
WHERE exhino = #{exhino}

UPDATE exhi 
SET visible='Y'
WHERE exhino=#{exhino}

UPDATE exhi 
SET visible='N'
WHERE exhino=#{exhino}

UPDATE exhi 
SET cnt = cnt + 1 
WHERE exhino = #{exhino}

UPDATE exhi 
SET cnt = cnt - 1 
WHERE exhino = #{exhino}

UPDATE exhi
SET visible=#{visible}
WHERE exhino=#{exhino}

UPDATE exhi 
SET cnt = cnt + 1 
WHERE exhino=#{exhino}

UPDATE exhi 
SET cnt = cnt - 1 
WHERE exhino=#{exhino}
