mapper.update:
UPDATE gallery 
SET map=#{map} 
WHERE galleryno=#{galleryno}

UPDATE gallery 
SET youtube=#{youtube} 
WHERE galleryno=#{galleryno}

mapper.select:
SELECT COUNT(*) as cnt FROM gallery
WHERE exhino=#{exhino}
WHERE exhino=#{exhino} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT galleryno, adminno, exhino, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, r FROM ( SELECT galleryno, adminno, exhino, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, rownum as r FROM ( SELECT galleryno, adminno, exhino, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube FROM gallery
WHERE exhino=#{exhino}
WHERE exhino=#{exhino} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY galleryno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

mapper.update:
UPDATE gallery
SET title=#{title}, content=#{content},  word=#{word} 
WHERE galleryno = #{galleryno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM gallery
WHERE galleryno = #{galleryno} AND passwd=#{passwd}

mapper.update:
UPDATE gallery
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE galleryno = #{galleryno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM gallery 
WHERE exhino=#{exhino}

mapper.update:
UPDATE gallery
SET replycnt = replycnt + 1
WHERE galleryno = #{galleryno}

UPDATE gallery
SET replycnt = replycnt - 1
WHERE galleryno = #{galleryno}
