mapper.select:
SELECT notesno, adminno, title, content, rdate, r FROM ( SELECT notesno, adminno, title, content, rdate, rownum as r FROM ( SELECT notesno, adminno, title, content, rdate FROM notes
WHERE (UPPER(title) LIKE '%' || #{word} || '%' OR UPPER(content) LIKE '%' || #{word} || '%' OR UPPER(word) LIKE '%' || #{word} || '%')
ORDER BY notesno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt
FROM notes
WHERE notesno = #{notesno} AND passwd = #{passwd}

mapper.update:
UPDATE notes
SET title=#{title}, content=#{content}, word=#{word}
WHERE notesno = #{notesno}

UPDATE notes
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE notesno = #{notesno}

update notes
set cnt = cnt + 1
where notesno = #{notesno}
